package basic1;

public class perfectno {

	public static void main(String[] args) {
		int sum=0,no=8;
		for(int i=1;i<no;i++)
		{
			if(no%i==0)
			{
			sum=sum+i;
			}}
				if(sum==no)
				{
				
					System.out.println("PERFECT");
				
			}
			
		}
		
		// TODO Auto-generated method stub

	}


